#!/bin/bash
SERVER=$(hostname -s) REMOTE="opendrive-sina:${SERVER}" FILTER="/root/filter.txt" LOGFILE="/root/backup.sh.log" VERBOSE="-v" # Add more v's for more verbosity VERSIONS=20 # Number of backup versions to keep
# Purge oldest version VERSION=$VERSIONS rclone purge ${REMOTE}/old${VERSION} ${VERBOSE}
# Make room for a new backup version while [ $VERSION -gt 1 ]; do (( OLDV=$VERSION-1 )) rclone move ${REMOTE}/old${OLDV} ${REMOTE}/old${VERSION} ${VERBOSE} (( VERSION = $OLDV )) done
# Perform backup rclone -L ${VERBOSE} --log-file=${LOGFILE} sync /etc/letsencrypt/ ${REMOTE}/letsencrypt/ rclone -L ${VERBOSE} --log-file=${LOGFILE} sync /etc/nginx/ ${REMOTE}/nginx/ rclone ${VERBOSE} --log-file=${LOGFILE} sync /etc/network/interfaces ${REMOTE}/etc/ rclone ${VERBOSE} --log-file=${LOGFILE} sync /var/lib/vz/dump/ ${REMOTE}/recent --backup-dir ${REMOTE}/old1 --skip-links
